    public void raiseArm()
    {
    	if (weAreInManualMode) {
    		logger.info("flipping into automatic");
    		armCANTalon.changeControlMode(TalonControlMode.Position);
    		weAreInManualMode = false;
    	}
    	armCANTalon.setSetpoint(1);
    	logger.info("setting setpoint 1");
    }
    public void lowerArm()
    {
    	if (weAreInManualMode) {
    		logger.info("flipping into automatic");
    		armCANTalon.changeControlMode(TalonControlMode.Position);
    		weAreInManualMode = false;
    	}
    	armCANTalon.setSetpoint(0);
    	logger.info("setting setpoint 0");
    }
    public void goIntoAutomaticMode() {
    	if (weAreInManualMode) {
    		logger.info("flipping into automatic");
    		armCANTalon.changeControlMode(TalonControlMode.Position);
    		weAreInManualMode = false;
    		armCANTalon.setSetpoint(armCANTalon.getEncPosition());
    	}
    }
    public void setManualPower(double power)
    {
    	if (!weAreInManualMode) {
    		logger.info("flipping into manual");
    		// TODO double check this - Voltage or VBus?
    		armCANTalon.changeControlMode(TalonControlMode.Voltage);
    		weAreInManualMode = true;
    		
    	}
    	armCANTalon.set(power);
    	logger.debug("arm power = {}", power);
    }
